import React, { useState } from 'react';

function CarItem({ car, viewCarList }) {
    const [successMessage, setSuccessMessage] = useState(""); // Stare pentru mesajul de succes

    const handleAddToCart = () => {
        const userId = localStorage.getItem('idUser'); // Obține id-ul utilizatorului din localStorage
        if (!userId) {
            setSuccessMessage("User ID nu este disponibil.");
            return;
        }

        // Obține ID-ul coșului clientului
        fetch(`http://localhost:8080/api/client/${userId}`)
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Eroare la obținerea datelor clientului");
                }
                return response.json();
            })
            .then((client) => {
                if (!client.cart || !client.cart.idCart) {
                    throw new Error("Coșul nu este disponibil pentru acest client");
                }

                const cartId = client.cart.idCart;

                // Trimite cererea pentru a adăuga mașina în coș
                return fetch(`http://localhost:8080/api/cart/${cartId}/add-car/${car.idCar}`, {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                });
            })
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Eroare la adăugarea mașinii în coș");
                }
                setSuccessMessage("Mașina a fost adăugată în coș cu succes!"); // Setează mesajul de succes
            })
            .catch((error) => {
                console.error("Eroare:", error);
                setSuccessMessage("A apărut o problemă: " + error.message); // Mesaj de eroare
            });
    };

    return (
        <div className="car-item">
            <div className="car-image">
                {car.imageUrl ? (
                    <img src={car.imageUrl} alt={car.model} />
                ) : (
                    <p>Imagine indisponibilă</p>
                )}
            </div>
            <div className="car-info">
                <h3>{car.model}</h3>
                <p>Kilometers: {car.kilometers}</p>
                <p>Release Date: {car.releaseDate}</p>
                <p>Vehicle Type: {car.vehicleType}</p>
                <p>Price: {car.price} €</p>
                <p>Color: {car.color}</p>
                {viewCarList && <button onClick={handleAddToCart}>Adaugă în Coș</button>}
                {/* Afișează mesajul de succes */}
                {successMessage && <p className="success-message">{successMessage}</p>}
            </div>
        </div>
    );
}

export default CarItem;
