import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import CarItem from './CarItem';

function ShowroomDetail() {
    const { id } = useParams();
    const [cars, setCars] = useState([]);
    const [showroomName, setShowroomName] = useState("");

    useEffect(() => {
        // Fetch showroom name
        fetch(`http://localhost:8080/api/showroom/${id}`)
            .then(response => response.json())
            .then(data => setShowroomName(data.name))
            .catch(error => console.error("Eroare:", error));

        // Fetch cars in the showroom
        fetch(`http://localhost:8080/api/showroom/${id}/cars`)
            .then(response => response.json())
            .then(data => setCars(data))
            .catch(error => console.error("Eroare:", error));
    }, [id]);

    return (
        <div>
            <h2>{showroomName}</h2>
            <div className="car-list">
                {cars.length > 0 ? (
                    cars.map((car, index) => (
                        <CarItem key={index} car={car} />
                    ))
                ) : (
                    <p>Nu există mașini disponibile pentru acest showroom.</p>
                )}
            </div>
        </div>
    );
}

export default ShowroomDetail;
