import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import CarItem from './CarItem';

function ViewCart() {
    const [cart, setCart] = useState([]); // Lista de mașini din coș
    const [loading, setLoading] = useState(true); // Stare pentru încărcare
    const [error, setError] = useState(null); // Stare pentru erori
    const navigate = useNavigate();
    const userId = localStorage.getItem('idUser'); // Preluare idUser din localStorage

    useEffect(() => {
        if (!userId) {
            setError("User ID nu este disponibil.");
            setLoading(false);
            return;
        }

        // Obține datele clientului pe baza idUser și apoi detaliile coșului
        fetch(`http://localhost:8080/api/client/${userId}`)
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Eroare la obținerea datelor clientului");
                }
                return response.json();
            })
            .then((client) => {
                console.log(client);
                if (!client.cart || !client.cart.idCart) {
                    throw new Error("Coșul nu este disponibil pentru acest client");
                }
                return fetch(`http://localhost:8080/api/cart/${client.cart.idCart}`);
            })
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Eroare la încărcarea coșului");
                }
                return response.json();
            })
            .then((data) => {
                setCart(data.cars || []); // Presupunem că răspunsul include un câmp `cars`
            })
            .catch((error) => {
                console.error("Eroare:", error);
                setError(error.message);
            })
            .finally(() => {
                setLoading(false); // Oprește afișarea mesajului de încărcare
            });
    }, [userId]);

    const handleGoToOrder = () => {
        navigate('/order'); // Navighează la pagina de comandă
    };

    if (loading) {
        return <p>Se încarcă coșul...</p>;
    }

    if (error) {
        return <p>Eroare: {error}</p>;
    }

    return (
        <div>
            <h2>Vezi Coșul</h2>
            <p>Aici poți găsi date despre coșul tău</p>
            {cart.length > 0 ? (
                <div className="cart-items">
                    {cart.map((car) => (
                        <CarItem key={car.idCar} car={car} /> // Afișează fiecare mașină folosind CarItem
                    ))}
                </div>
            ) : (
                <p>Coșul este gol.</p>
            )}
            <button onClick={handleGoToOrder}>Comandă</button>
        </div>
    );
}

export default ViewCart;
