import { useEffect, useState } from 'react';
import React from 'react';
import ShowroomItem from './ShowroomItem';
import '../Styles/ShowroomList.css';

function ShowroomList() {
    const [showrooms, setShowrooms] = useState([]);

    const handleButtonClick = () => {
        console.log("Add Showroom button clicked");
    };

    useEffect(() => {
        fetch("http://localhost:8080/api/showroom")
            .then(response => {
                if (!response.ok) {
                    throw new Error("Eroare la încărcarea datelor");
                }
                return response.json();
            })
            .then(data => {
                console.log("Data received from backend:", data);
                setShowrooms(data);
            })
            .catch(error => console.error("Eroare:", error));
    }, []);

    return (
        <div>
            <h2>Showroom-urile Disponibile</h2>
            <div className="button-container">
                <button className="buttonAddShowroom" onClick={handleButtonClick}>Add Showroom</button>
            </div>
            <div className="showroom-list">
                {showrooms.length > 0 ? (
                    showrooms.map((showroom, index) => (
                        <ShowroomItem key={index} showroom={showroom} />
                    ))
                ) : (
                    <p>Nu există showroom-uri disponibile.</p>
                )}
            </div>
        </div>
    );
}

export default ShowroomList;
