import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import CarItem from './CarItem';

function ShowroomDetail() {
    const { id } = useParams(); // ID-ul showroom-ului selectat
    const [cars, setCars] = useState([]);
    const [filteredCars, setFilteredCars] = useState([]);
    const [showroomName, setShowroomName] = useState("");

    useEffect(() => {
        // Fetch toate mașinile
        fetch("http://localhost:8080/api/car")
            .then(response => response.json())
            .then(data => {
                setCars(data);
                const filtered = data.filter(car => car.showroom.idShowroom === parseInt(id));
                setFilteredCars(filtered);
            })
            .catch(error => console.error("Eroare la încărcarea mașinilor:", error));

        // Fetch showroom name
        fetch(`http://localhost:8080/api/showroom/${id}`)
            .then(response => response.json())
            .then(data => setShowroomName(data.name))
            .catch(error => console.error("Eroare la încărcarea showroom-ului:", error));
    }, [id]);

    return (
        <div>
            <h2>{showroomName}</h2>
            <div className="car-list">
                {filteredCars.length > 0 ? (
                    filteredCars.map((car) => (
                        <CarItem key={car.idCar} car={car} />
                    ))
                ) : (
                    <p>Nu există mașini disponibile pentru acest showroom.</p>
                )}
            </div>
        </div>
    );
}

export default ShowroomDetail;
