import React from 'react';
import CarList from './Components/CarList';
import './App.css';

function App() {
  return (
      <div className="App">
        <header>
          <h1>Dealer Auto</h1>
          <p>Cele mai bune oferte de mașini noi și rulate!</p>
        </header>
        <main>
          <CarList />
        </main>
        <footer>
          <p>&copy; 2024 Dealer Auto. Toate drepturile rezervate.</p>
        </footer>
      </div>
  );
    const [showForm, setShowForm] = useState(false);

    const handleAddCar = (car) => {
        console.log("Mașină adăugată:", car);
        // Aici poți trimite datele mașinii la backend pentru a o salva în baza de date
        setShowForm(false); // Ascunde formularul după adăugare
    };

    return (
        <div className="App">
            <header>
                <h1>Dealer Auto</h1>
                <p>Cele mai bune oferte de mașini noi și rulate!</p>
            </header>
            <main>
                <button onClick={() => setShowForm(!showForm)}>
                    {showForm ? 'Anulează' : 'Adaugă o nouă mașină'}
                </button>

                {/* Afișează componenta AddCarForm doar dacă showForm este true */}
                {showForm && <AddCarForm onSubmit={handleAddCar} />}
            </main>
            <footer>
                <p>&copy; 2024 Dealer Auto. Toate drepturile rezervate.</p>
            </footer>
        </div>
    );
}

export default App;
