import React, { useEffect, useState } from 'react';

function Favorites() {
    const [favorites, setFavorites] = useState([]);
    const [error, setError] = useState(null);
    const userId = localStorage.getItem('idUser');

    useEffect(() => {
        if (userId) {
            fetch(`http://localhost:8080/api/favorites/${userId}`)
                .then((response) => {
                    if (!response.ok) throw new Error("Eroare la încărcarea mașinilor favorite.");
                    return response.json();
                })
                .then((data) => {
                    console.log("Date favorite:", data); // Log pentru debugging
                    setFavorites(data);
                })
                .catch((err) => setError(err.message));
        }
    }, [userId]);

    return (
        <div>
            <h2>Mașinile Favorite</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {favorites.length > 0 ? (
                <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
                    {favorites.map((favorite) => (
                        <div
                            key={favorite.id}
                            style={{
                                border: '1px solid #ddd',
                                borderRadius: '8px',
                                padding: '10px',
                                width: '300px',
                                boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
                            }}
                        >

                            <h3>{favorite.car.model}</h3>
                            <img
                                src={favorite.car.imageUrl}
                                alt={favorite.car.model}
                                style={{width: '100%', borderRadius: '8px'}}
                            />
                            <p>Price: {favorite.car.price} €</p>
                            <p>Color: {favorite.car.color}</p>
                            <p>Kilometers: {favorite.car.kilometers}</p>
                            <p>Release Date: {favorite.car.releaseDate}</p>
                        </div>
                    ))}
                </div>
            ) : (
                <p>Nu ai adăugat nicio mașină la favorite.</p>
            )}
        </div>
    );
}

export default Favorites;
