import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import CarItem from './CarItem';

function ViewCart({ idUser }) {
    const [cart, setCart] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        // Verifică dacă idUser este disponibil
        if (!idUser) {
            setError("User ID nu este disponibil.");
            setLoading(false);
            return;
        }

        // Obține idClient pe baza idUser și apoi coșul clientului
        fetch(`http://localhost:8080/api/client/${idUser}`)
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Eroare la obținerea ID-ului clientului");
                }
                return response.json();
            })
            .then((idClient) => {
                // Folosește idClient pentru a obține detaliile coșului
                return fetch(`http://localhost:8080/api/cart/${idClient}`);
            })
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Eroare la încărcarea coșului");
                }
                return response.json();
            })
            .then((data) => {
                setCart(data.cars || []); // Presupunem că răspunsul include un câmp `cars`
            })
            .catch((error) => {
                console.error("Eroare:", error);
                setError(error.message);
            })
            .finally(() => {
                setLoading(false);
            });
    }, [idUser]);

    const handleGoToOrder = () => {
        navigate('/order');
    };

    if (loading) {
        return <p>Se încarcă coșul...</p>;
    }

    if (error) {
        return <p>Eroare: {error}</p>;
    }

    return (
        <div>
            <h2>Vezi Coșul</h2>
            <p>Aici poți găsi date despre coșul tău</p>
            {cart.length > 0 ? (
                <div className="cart-items">
                    {cart.map((car) => (
                        <CarItem key={car.idCar} car={car} />
                    ))}
                </div>
            ) : (
                <p>Coșul este gol.</p>
            )}
            <button onClick={handleGoToOrder} disabled={cart.length === 0}>
                Comandă
            </button>
        </div>
    );
}

export default ViewCart;
