import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import ShowroomItem from './ShowroomItem';
import '../Styles/ShowroomList.css';

function ShowroomList() {
    const [showrooms, setShowrooms] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        fetch("http://localhost:8080/api/showroom")
            .then(response => {
                if (!response.ok) {
                    throw new Error("Eroare la încărcarea datelor");
                }
                return response.json();
            })
            .then(data => setShowrooms(data))
            .catch(error => console.error("Eroare:", error));
    }, []);

    const handleShowroomClick = (id) => {
        navigate(`/showroom/${id}`);
    };

    return (
        <div>
            <h2>Showroom-urile Disponibile</h2>
            <div className="showroom-list">
                {showrooms.length > 0 ? (
                    showrooms.map((showroom) => (
                        <div
                            key={showroom.id}
                            className="showroom-item-container"
                        >
                            <ShowroomItem
                                showroom={showroom}
                                onClick={() => handleShowroomClick(showroom.id)}
                            />
                        </div>
                    ))
                ) : (
                    <p>Nu există showroom-uri disponibile.</p>
                )}
            </div>
        </div>
    );
}

export default ShowroomList;
