import React, { useEffect, useState } from 'react';

function CarHistory() {
    const [payments, setPayments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const userId = localStorage.getItem('idUser');

    useEffect(() => {
        if (!userId) {
            setError("User ID nu este disponibil.");
            setLoading(false);
            return;
        }

        fetch(`http://localhost:8080/api/payment/user/${userId}`)
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Eroare la încărcarea plăților.");
                }
                return response.json();
            })
            .then((data) => {
                // Asigură-te că datele sunt întotdeauna un array
                setPayments(Array.isArray(data) ? data : [data]);
            })

            .catch((error) => {
                console.error("Eroare:", error);
                setError(error.message);
            })
            .finally(() => {
                setLoading(false);
            });
    }, [userId]);

    useEffect(() => {
        console.log("Payments updated:", payments);
    }, [payments]);

    if (loading) {
        return <p>Se încarcă istoricul...</p>;
    }

    if (error) {
        return <p>Eroare: {error}</p>;
    }

    return (
        <div>
            <h2>Istoric Plăți</h2>
            <p>Aici poți găsi date despre plățile asociate coșului tău:</p>
            {payments.length > 0 ? (
                <div>
                    {payments.map((payment) => (
                        <div key={payment.idPayment} className="payment-item">
                            <p>ID Plată: {payment.idPayment}</p>
                            <p>Adresa de livrare: {payment.shippingAddress}</p>
                            <p>Data livrării: {payment.dateOfDelivery}</p>
                            <hr />
                        </div>
                    ))}
                </div>
            ) : (
                <p>Nu există plăți înregistrate.</p>
            )}
        </div>
    );
}

export default CarHistory;
