import { useEffect, useState } from 'react';
import React from 'react';
import CarItem from './CarItem';
import '../Styles/CarManagement.css';
import car1 from '../assets/images/car1.jpg';
import car2 from '../assets/images/car2.jpg';
import car3 from '../assets/images/car3.jpg';
import car4 from '../assets/images/car4.jpg';
import car5 from '../assets/images/car5.jpg';

const carImages = [car1, car2, car3, car4, car5];

function CarManagement() {
    const [cars, setCars] = useState([]);
    const [formData, setFormData] = useState({
        kilometers: "",
        releaseDate: "",
        model: "",
        vehicleType: "",
        price: "",
        color: "",
        showroomId: "",
    });
    const [showAddForm, setShowAddForm] = useState(false);
    const [editMode, setEditMode] = useState(false);
    const [editCarId, setEditCarId] = useState(null);

    useEffect(() => {
        fetch("http://localhost:8080/api/car")
            .then(response => {
                if (!response.ok) {
                    throw new Error("Eroare la încărcarea datelor");
                }
                return response.json();
            })
            .then(data => {
                const carsWithImages = data.map((car, index) => ({
                    ...car,
                    imageUrl: carImages[index % carImages.length]
                }));
                setCars(carsWithImages);
            })
            .catch(error => console.error("Eroare:", error));
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleSubmit = () => {
        const carData = {
            kilometers: parseInt(formData.kilometers, 10),
            releaseDate: formData.releaseDate,
            model: formData.model,
            vehicleType: formData.vehicleType,
            price: parseInt(formData.price, 10),
            color: formData.color,
        };

        const url = editMode
            ? `http://localhost:8080/api/car/${editCarId}`
            : `http://localhost:8080/api/car`;
        const method = editMode ? "PUT" : "POST";
        const params = `?showroomId=${formData.showroomId}`;

        fetch(url + params, {
            method: method,
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(carData),
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error("Eroare la trimiterea datelor");
                }
                return response.json();
            })
            .then(() => {
                setFormData({
                    kilometers: "",
                    releaseDate: "",
                    model: "",
                    vehicleType: "",
                    price: "",
                    color: "",
                    showroomId: "",
                });
                setShowAddForm(false);
                setEditMode(false);
                setEditCarId(null);
                fetch("http://localhost:8080/api/car")
                    .then(response => response.json())
                    .then(data => {
                        const carsWithImages = data.map((car, index) => ({
                            ...car,
                            imageUrl: carImages[index % carImages.length],
                        }));
                        setCars(carsWithImages);
                    });
            })
            .catch(error => console.error("Eroare la trimiterea datelor:", error));
    };

    const handleDelete = (idCar) => {
        fetch(`http://localhost:8080/api/car/${idCar}`, {
            method: "DELETE",
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error("Eroare la ștergerea mașinii");
                }
                setCars(cars.filter(car => car.idCar !== idCar));
            })
            .catch(error => console.error("Eroare la ștergerea mașinii:", error));
    };

    const handleEdit = (car) => {
        setFormData({
            kilometers: car.kilometers,
            releaseDate: car.releaseDate,
            model: car.model,
            vehicleType: car.vehicleType,
            price: car.price,
            color: car.color,
            showroomId: car.showroom.idShowroom,
        });
        setEditMode(true);
        setEditCarId(car.idCar);
        setShowAddForm(true);
    };

    return (
        <div>
            <h3>Management Mașini</h3>

            {/* Buton pentru afișarea formularului */}
            <div className="button-container">
                <button onClick={() => setShowAddForm(!showAddForm)}>
                    {showAddForm ? "Anulează Adăugarea Mașinii" : "Adaugă Mașină"}
                </button>
            </div>

            {/* Formular pentru adăugarea unei mașini */}
            {showAddForm && (
                <div className="add-form">
                    <h3>{editMode ? "Editează Mașina" : "Adaugă Mașină Nouă"}</h3>
                    <label>
                        Kilometri:
                        <input
                            type="number"
                            name="kilometers"
                            value={formData.kilometers}
                            onChange={handleInputChange}
                        />
                    </label>
                    <label>
                        Data Lansării:
                        <input
                            type="text"
                            name="releaseDate"
                            value={formData.releaseDate}
                            onChange={handleInputChange}
                            placeholder="14-11-2014" // adaugă un placeholder pentru a sugera formatul
                        />
                    </label>
                    <label>
                        Model:
                        <input
                            type="text"
                            name="model"
                            value={formData.model}
                            onChange={handleInputChange}
                        />
                    </label>
                    <label>
                        Tip Vehicul:
                        <input
                            type="text"
                            name="vehicleType"
                            value={formData.vehicleType}
                            onChange={handleInputChange}
                        />
                    </label>
                    <label>
                        Preț:
                        <input
                            type="number"
                            name="price"
                            value={formData.price}
                            onChange={handleInputChange}
                        />
                    </label>
                    <label>
                        Culoare:
                        <input
                            type="text"
                            name="color"
                            value={formData.color}
                            onChange={handleInputChange}
                        />
                    </label>
                    <label>
                        ID Showroom:
                        <input
                            type="number"
                            name="showroomId"
                            value={formData.showroomId}
                            onChange={handleInputChange}
                        />
                    </label>
                    <button onClick={handleSubmit}>{editMode ? "Actualizează" : "Trimite"}</button>
                    <button onClick={() => {
                        setShowAddForm(false);
                        setEditMode(false);
                        setEditCarId(null);
                    }}>Anulează</button>
                </div>
            )}

            {/* Listarea mașinilor */}
            <div className="car-list">
                {cars.length > 0 ? (
                    cars.map((car) => (
                        <div key={car.idCar} className="car-item-container">
                            <CarItem car={car} imageUrl={car.imageUrl} />
                            <div className="car-item-buttons">
                                <button onClick={() => handleEdit(car)}>Editează</button>
                                <button onClick={() => handleDelete(car.idCar)}>Șterge</button>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>Nu există mașini disponibile.</p>
                )}
            </div>
        </div>
    );
}

export default CarManagement;
