import { useEffect, useState } from 'react';
import React from 'react';
import ShowroomItem from './ShowroomItem';
import '../Styles/ShowroomList.css';

function ShowroomList({ userRole }) {
    const [showrooms, setShowrooms] = useState([]);

    const handleAddShowroom = () => {
        console.log("Add Showroom button clicked");
    };

    const handleUpdateShowroom = (showroomId) => {
        console.log(`Update Showroom with ID ${showroomId} clicked`);
    };

    const handleDeleteShowroom = (showroomId) => {
        console.log(`Delete Showroom with ID ${showroomId} clicked`);
    };

    useEffect(() => {
        fetch("http://localhost:8080/api/showroom")
            .then(response => {
                if (!response.ok) {
                    throw new Error("Eroare la încărcarea datelor");
                }
                return response.json();
            })
            .then(data => {
                console.log("Data received from backend:", data);
                setShowrooms(data);
            })
            .catch(error => console.error("Eroare:", error));
    }, []);

    return (
        <div>
            <h2>Showroom-urile Disponibile</h2>
            {userRole === "Admin" && (
                <div className="admin-buttons">
                    <button className="buttonAddShowroom" onClick={handleAddShowroom}>Add Showroom</button>
                    <button
                        className="buttonUpdateShowroom"
                        onClick={() => handleUpdateShowroom(null)}>Update Showroom
                    </button>
                    <button
                        className="buttonDeleteShowroom"
                        onClick={() => handleDeleteShowroom(null)}>Delete Showroom
                    </button>
                </div>
            )}
            <div className="showroom-list">
                {showrooms.length > 0 ? (
                    showrooms.map((showroom, index) => (
                        <div key={index} className="showroom-item-container">
                            <ShowroomItem showroom={showroom} />
                            {userRole === "Admin" && (
                                <div className="admin-buttons">
                                    <button
                                        className="buttonUpdateShowroom"
                                        onClick={() => handleUpdateShowroom(showroom.idShowroom)}
                                    >
                                        Update Showroom
                                    </button>
                                    <button
                                        className="buttonDeleteShowroom"
                                        onClick={() => handleDeleteShowroom(showroom.idShowroom)}
                                    >
                                        Delete Showroom
                                    </button>
                                </div>
                            )}
                        </div>
                    ))
                ) : (
                    <p>Nu există showroom-uri disponibile.</p>
                )}
            </div>
        </div>
    );
}

export default ShowroomList;
