import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../Styles/LogIn.css';

function LogIn({setAuthenticated}) {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();
    const handleSubmit = (event) => {
        event.preventDefault();
        const credentials = {
            username: username,
            password: password
        };
        fetch('http://localhost:8080/api/auth/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(credentials)
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Eroare la autentificare');
                }
                return response.json();
            })
            .then(data => {
                console.log('Autentificare reușită:', data);
                // Stochează starea de autentificare (de exemplu, un token)
                localStorage.setItem('authToken', data.token);
                setAuthenticated(true); // Setează starea de autentificare
                navigate('/');
            })
            .catch(error => {
                console.error('Eroare:', error);
                // Aici poți afișa un mesaj de eroare utilizatorului
            });
    };


    return (
        <div className="login-container">
            <h2>Autentificare</h2>
            <p>Introdu datele necesare pentru a te autentifica:</p>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="username">Nume utilizator sau Email:</label>
                    <input
                        type="text"
                        id="username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        placeholder="Introdu numele de utilizator sau email-ul"
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Parolă:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Introdu parola"
                        required
                    />
                </div>
                <button type="submit" className="login-button">Autentificare</button>
            </form>
        </div>
    );
}

export default LogIn;
