import React, { useEffect, useState } from 'react';

function ClientManagement() {
    const [clients, setClients] = useState([]);
    const [newClient, setNewClient] = useState({ name: '', email: '', phone: '' });

    // Fetch clients from API
    useEffect(() => {
        fetch("http://localhost:8080/api/clients")
            .then(response => response.json())
            .then(data => setClients(data))
            .catch(error => console.error("Eroare la încărcarea clienților:", error));
    }, []);

    // Handle input changes for the new client form
    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setNewClient({ ...newClient, [name]: value });
    };

    // Add a new client
    const handleAddClient = () => {
        fetch("http://localhost:8080/api/clients", {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(newClient),
        })
            .then(response => response.json())
            .then(addedClient => {
                setClients([...clients, addedClient]);
                setNewClient({ name: '', email: '', phone: '' });
            })
            .catch(error => console.error("Eroare la adăugarea clientului:", error));
    };

    // Delete a client
    const handleDeleteClient = (id) => {
        fetch(`http://localhost:8080/api/clients/${id}`, { method: 'DELETE' })
            .then(() => setClients(clients.filter(client => client.id !== id)))
            .catch(error => console.error("Eroare la ștergerea clientului:", error));
    };

    return (
        <div>
            <h2>Management Clienți</h2>
            <p>Aici poți gestiona clienții</p>

            {/* Form to Add New Client */}
            <div>
                <h3>Adaugă un Client Nou</h3>
                <input
                    type="text"
                    name="name"
                    placeholder="Nume"
                    value={newClient.name}
                    onChange={handleInputChange}
                />
                <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={newClient.email}
                    onChange={handleInputChange}
                />
                <input
                    type="text"
                    name="phone"
                    placeholder="Telefon"
                    value={newClient.phone}
                    onChange={handleInputChange}
                />
                <button onClick={handleAddClient}>Adaugă Client</button>
            </div>

            {/* Client List */}
            <div>
                <h3>Lista Clienților</h3>
                {clients.length > 0 ? (
                    <ul>
                        {clients.map(client => (
                            <li key={client.id}>
                                <strong>{client.name}</strong> - {client.email} - {client.phone}
                                <button onClick={() => handleDeleteClient(client.id)}>Șterge</button>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>Nu există clienți înregistrați.</p>
                )}
            </div>
        </div>
    );
}

export default ClientManagement;
