import React, { useEffect, useState } from 'react';
import CarItem from './CarItem';

function CarList() {
    const [cars, setCars] = useState([]);

    useEffect(() => {
        fetch("http://localhost:8080/api/v1/car")
            .then(response => {
                if (!response.ok) {
                    throw new Error("Eroare la încărcarea datelor");
                }
                return response.json();
            })
            .then(data => {
                console.log("Data received from backend:", data); // Log pentru verificare
                setCars(data);
            })
            .catch(error => console.error("Eroare:", error));
    }, []);

    return (
        <div>
            <h2>Mașinile Disponibile</h2>
            <div className="car-list">
                {cars.length > 0 ? (
                    cars.map((car, index) => (
                        <CarItem key={index} car={car} />
                    ))
                ) : (
                    <p>Nu există mașini disponibile.</p>
                )}
            </div>
        </div>
    );
}
function AddCarForm({ onSubmit }) {
    const [car, setCar] = useState({


        kilometers: '',
        releaseDate:'',
        model: '',
        vehicleType: '',
        price: '',
        color: '',
        idShowroom:'',
    });
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setCar((prevCar) => ({
            ...prevCar,
            [name]: value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSubmit(car); // Trimite datele către componenta părinte
        setCar({ model: '', year: '', color: '', price: '' });
    };

    return (
        <form onSubmit={handleSubmit} className="add-car-form">
            <div>
                <label>Model:</label>
                <input
                    type="text"
                    name="model"
                    value={car.model}
                    onChange={handleInputChange}
                    required
                />
            </div>
            <div>
                <label>An fabricație:</label>
                <input
                    type="number"
                    name="year"
                    value={car.year}
                    onChange={handleInputChange}
                    required
                />
            </div>
            <div>
                <label>Culoare:</label>
                <input
                    type="text"
                    name="color"
                    value={car.color}
                    onChange={handleInputChange}
                    required
                />
            </div>
            <div>
                <label>Preț (€):</label>
                <input
                    type="number"
                    name="price"
                    value={car.price}
                    onChange={handleInputChange}
                    required
                />
            </div>
            <button type="submit">Adaugă mașina</button>
        </form>
    );
}
export default CarList;
