import React from 'react';
import car1 from '../assets/images/car1.jpg';
import car2 from '../assets/images/car2.jpg';
import car3 from '../assets/images/car3.jpg';
import car4 from '../assets/images/car4.jpg';
import car5 from '../assets/images/car5.jpg';

function CarItem({car, viewCarList, onAddToCart }) {
    const handleAddToCart = (carId) => {
        const userId = localStorage.getItem('idUser'); // Obține id-ul utilizatorului din localStorage
        if (!userId) {
            alert("User ID nu este disponibil.");
            return;
        }

        // Obține ID-ul coșului clientului
        fetch(`http://localhost:8080/api/client/${userId}`)
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Eroare la obținerea datelor clientului");
                }
                return response.json();
            })
            .then((client) => {
                if (!client.cart || !client.cart.idCart) {
                    throw new Error("Coșul nu este disponibil pentru acest client");
                }

                const cartId = client.cart.idCart;

                // Trimite cererea pentru a adăuga mașina în coș
                return fetch(`http://localhost:8080/api/cart/${cartId}/add-car/${carId}`, {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                });
            })
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Eroare la adăugarea mașinii în coș");
                }
                alert("Mașina a fost adăugată în coș cu succes!");
            })
            .catch((error) => {
                console.error("Eroare:", error);
                alert("A apărut o problemă: " + error.message);
            });
    };

    return (
        <div className="car-item">
            <div className="car-image">
                {/* Afișează imaginea doar dacă imageUrl este disponibil */}
                {car.imageUrl ? (
                    <img src={car.imageUrl} alt={car.model}/>
                ) : (
                    <p>Imagine indisponibilă</p>
                )}
            </div>
            <div className="car-info">
                <h3>{car.model}</h3>
                <p>Kilometers: {car.kilometers}</p>
                <p>Release Date: {car.releaseDate}</p>
                <p>Vehicle Type: {car.vehicleType}</p>
                <p>Price: {car.price} €</p>
                <p>Color: {car.color}</p>
                {viewCarList && <button onClick={() => onAddToCart(car.idCar)}>Adauga in Cos</button>}
            </div>
        </div>
    );
}

export default CarItem;
