import { useEffect, useState } from 'react';
import React from 'react';
import CarItem from './CarItem';
import '../Styles/CarManagement.css';
import car1 from '../assets/images/car1.jpg';
import car2 from '../assets/images/car2.jpg';
import car3 from '../assets/images/car3.jpg';
import car4 from '../assets/images/car4.jpg';
import car5 from '../assets/images/car5.jpg';

const carImages = [car1, car2, car3, car4, car5];

function CarManagement() {
    const [cars, setCars] = useState([]);
    const [newCar, setNewCar] = useState({ model: '', kilometers: 0, price: 0 });
    const [updateCarId, setUpdateCarId] = useState(null);
    const [updateData, setUpdateData] = useState({ kilometers: 0, price: 0 });
    const [showAddForm, setShowAddForm] = useState(false);

    useEffect(() => {
        fetch("http://localhost:8080/api/car")
            .then(response => {
                if (!response.ok) {
                    throw new Error("Eroare la încărcarea datelor");
                }
                return response.json();
            })
            .then(data => {
                const carsWithImages = data.map((car, index) => ({
                    ...car,
                    imageUrl: index < carImages.length ? carImages[index] : null
                }));
                setCars(carsWithImages);
            })
            .catch(error => console.error("Eroare:", error));
    }, []);

    // Add Car
    const handleAddCar = () => {
        fetch("http://localhost:8080/api/car", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(newCar),
        })
            .then(() => {
                setCars([...cars, { ...newCar, idCar: cars.length + 1 }]);
                setNewCar({ model: '', kilometers: 0, price: 0 });
                setShowAddForm(false); // Hide the form after adding the car
            })
            .catch(error => console.error("Eroare la adăugarea mașinii:", error));
    };

    // Delete Car
    const handleDeleteCar = (id) => {
        fetch(`http://localhost:8080/api/car/${id}`, { method: "DELETE" })
            .then(() => setCars(cars.filter(car => car.idCar !== id)))
            .catch(error => console.error("Eroare la ștergerea mașinii:", error));
    };

    // Update Car
    const handleUpdateCar = () => {
        fetch(`http://localhost:8080/api/car/${updateCarId}`, {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(updateData),
        })
            .then(() => {
                setCars(
                    cars.map(car =>
                        car.idCar === updateCarId
                            ? { ...car, ...updateData }
                            : car
                    )
                );
                setUpdateCarId(null);
                setUpdateData({ kilometers: 0, price: 0 });
            })
            .catch(error => console.error("Eroare la actualizarea mașinii:", error));
    };

    return (
        <div>
            <h3>Management Mașini</h3>

            {/* Button to Show/Hide Add Car Form */}
            <div className="button-container">
                <button onClick={() => setShowAddForm(!showAddForm)}>
                    {showAddForm ? "Anulează Adăugarea" : "Adaugă Mașină"}
                </button>
            </div>

            {/* Add Car Form */}
            {showAddForm && (
                <div>
                    <h4>Adaugă o Mașină</h4>
                    <input
                        type="text"
                        placeholder="Model"
                        value={newCar.model}
                        onChange={(e) => setNewCar({ ...newCar, model: e.target.value })}
                    />
                    <input
                        type="number"
                        placeholder="Kilometri"
                        value={newCar.kilometers}
                        onChange={(e) => setNewCar({ ...newCar, kilometers: parseInt(e.target.value) })}
                    />
                    <input
                        type="number"
                        placeholder="Preț"
                        value={newCar.price}
                        onChange={(e) => setNewCar({ ...newCar, price: parseInt(e.target.value) })}
                    />
                    <button onClick={handleAddCar}>Adaugă Mașină</button>
                </div>
            )}

            {/* Update Car Form */}
            {updateCarId && (
                <div>
                    <h4>Actualizează Mașina</h4>
                    <input
                        type="number"
                        placeholder="Kilometri"
                        value={updateData.kilometers}
                        onChange={(e) =>
                            setUpdateData({ ...updateData, kilometers: parseInt(e.target.value) })
                        }
                    />
                    <input
                        type="number"
                        placeholder="Preț"
                        value={updateData.price}
                        onChange={(e) =>
                            setUpdateData({ ...updateData, price: parseInt(e.target.value) })
                        }
                    />
                    <button onClick={handleUpdateCar}>Actualizează</button>
                </div>
            )}

            {/* Car List */}
            <div className="car-list">
                {cars.length > 0 ? (
                    cars.map((car, index) => (
                        <div key={index} className="car-item">
                            <CarItem car={car} />
                            <button onClick={() => handleDeleteCar(car.idCar)}>Șterge</button>
                            <button onClick={() => setUpdateCarId(car.idCar)}>Actualizează</button>
                        </div>
                    ))
                ) : (
                    <p>Nu există mașini disponibile.</p>
                )}
            </div>
        </div>
    );
}

export default CarManagement;
