import { useEffect, useState } from 'react';
import React from 'react';
import ShowroomItem from './ShowroomItem';
import '../Styles/ShowroomList.css';
import car1 from '../assets/images/car1.jpg';
import car2 from '../assets/images/car2.jpg';
import car3 from '../assets/images/car3.jpg';
import car4 from '../assets/images/car4.jpg';
import car5 from '../assets/images/car5.jpg';

const carImages = [car1, car2, car3, car4, car5];

function ShowroomManagement() {
    const [showrooms, setShowrooms] = useState([]);
    const [formData, setFormData] = useState({
        name: "",
        location: "",
        phoneNumber: "",
        email: "",
        adminId: "",
    });
    const [showAddForm, setShowAddForm] = useState(false);
    const [isEditing, setIsEditing] = useState(false);
    const [editShowroomId, setEditShowroomId] = useState(null);

    useEffect(() => {
        fetchShowrooms();
    }, []);

    const fetchShowrooms = () => {
        fetch("http://localhost:8080/api/showroom")
            .then(response => {
                if (!response.ok) {
                    throw new Error("Eroare la încărcarea datelor");
                }
                return response.json();
            })
            .then(data => {
                const showroomsWithImages = data;
                setShowrooms(showroomsWithImages);
            })
            .catch(error => console.error("Eroare:", error));
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleSubmit = () => {
        const showroomData = {
            name: formData.name,
            location: formData.location,
            phoneNumber: formData.phoneNumber,
            email: formData.email,
        };

        if (isEditing) {
            const url = formData.adminId ? `http://localhost:8080/api/showroom/${editShowroomId}?adminId=${formData.adminId}` : `http://localhost:8080/api/showroom/${editShowroomId}`;

            fetch(url, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(showroomData),
            })
                .then(response => {
                    if (!response.ok) {
                        throw new Error("Eroare la actualizarea datelor");
                    }
                    return response.json();
                })
                .then(() => {
                    resetForm();
                    fetchShowrooms();
                })
                .catch(error => console.error("Eroare la actualizarea datelor:", error));
        } else {
            const url = `http://localhost:8080/api/showroom?adminId=${formData.adminId}`;

            fetch(url, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(showroomData),
            })
                .then(response => {
                    if (!response.ok) {
                        throw new Error("Eroare la trimiterea datelor");
                    }
                    return response.json();
                })
                .then(() => {
                    resetForm();
                    fetchShowrooms();
                })
                .catch(error => console.error("Eroare la trimiterea datelor:", error));
        }
    };

    const handleDelete = (adminId) => {
        fetch(`http://localhost:8080/api/showroom/${adminId}`, {
            method: "DELETE",
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error("Eroare la ștergerea showroom-ului");
                }
                setShowrooms(showrooms.filter(showroom => showroom.adminId !== adminId));
            })
            .catch(error => console.error("Eroare la ștergerea showroom-ului:", error));
    };

    const handleEdit = (showroom) => {
        setIsEditing(true);
        setEditShowroomId(showroom.adminId || "");
        setFormData({
            name: showroom.name || "",
            location: showroom.location || "",
            phoneNumber: showroom.phoneNumber || "",
            email: showroom.email || "",
            adminId: showroom.adminId || "",
        });
        setShowAddForm(true);
    };

    const handleCancelEdit = () => {
        resetForm();
    };

    const resetForm = () => {
        setFormData({
            name: "",
            location: "",
            phoneNumber: "",
            email: "",
            adminId: "",
        });
        setShowAddForm(false);
        setIsEditing(false);
        setEditShowroomId(null);
    };

    return (
        <div>
            <h3>Management Showroom-uri</h3>

            <div className="button-container">
                <button onClick={() => setShowAddForm(!showAddForm)}>
                    {showAddForm && !isEditing ? "Anulează" : "Adaugă Showroom"}
                </button>
            </div>
            {showAddForm && (
                <div className="add-form">
                    <h3>{isEditing ? "Editează Showroom" : "Adaugă Showroom Nou"}</h3>
                    <label>
                        Nume:
                        <input
                            type="text"
                            name="name"
                            value={formData.name}
                            onChange={handleInputChange}
                        />
                    </label>
                    <label>
                        Locație:
                        <input
                            type="text"
                            name="location"
                            value={formData.location}
                            onChange={handleInputChange}
                        />
                    </label>
                    <label>
                        Număr de telefon:
                        <input
                            type="text"
                            name="phoneNumber"
                            value={formData.phoneNumber}
                            onChange={handleInputChange}
                        />
                    </label>
                    <label>
                        Email:
                        <input
                            type="email"
                            name="email"
                            value={formData.email}
                            onChange={handleInputChange}
                        />
                    </label>
                    <label>
                        ID Admin:
                        <input
                            type="text"
                            name="adminId"
                            value={formData.adminId}
                            onChange={handleInputChange}
                        />
                    </label>
                    <button onClick={handleSubmit}>{isEditing ? "Actualizează" : "Trimite"}</button>
                    {isEditing && <button onClick={handleCancelEdit}>Anulează Editarea</button>}
                </div>
            )}

            <div className="showroom-list">
                {showrooms.length > 0 ? (
                    showrooms.map((showroom, index) => (
                        <div key={showroom.adminId || index} className="showroom-item-container">
                            <div className="showroom-details">

                                <p>Nume: {showroom.name}</p>
                                <p>Locație: {showroom.location}</p>
                                <p>Număr de telefon: {showroom.phoneNumber}</p>
                                <p>Email: {showroom.email}</p>
                            </div>
                            <div className="showroom-item-buttons">
                                <button onClick={() => handleEdit(showroom)}>Editează</button>
                                <button onClick={() => handleDelete(showroom.adminId)}>Șterge</button>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>Nu există showroom-uri disponibile.</p>
                )}
            </div>
        </div>
    );
}

export default ShowroomManagement;
