import React, { useEffect, useState } from 'react';
import '../Styles/CarHistory.css';

function CarHistory() {
    const [payments, setPayments] = useState([]);
    const [cars, setCars] = useState([]); // Mașinile asociate plăților
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const userId = localStorage.getItem('idUser');

    useEffect(() => {
        if (!userId) {
            setError("User ID nu este disponibil.");
            setLoading(false);
            return;
        }

        // Obține plățile
        fetch(`http://localhost:8080/api/payment/user/${userId}`)
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Eroare la încărcarea plăților.");
                }
                return response.json();
            })
            .then((data) => setPayments(data))
            .catch((error) => setError(error.message));

        // Obține mașinile din plăți
        fetch(`http://localhost:8080/api/payment/user/${userId}/cars`)
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Eroare la încărcarea mașinilor.");
                }
                return response.json();
            })
            .then((data) => setCars(data))
            .catch((error) => setError(error.message))
            .finally(() => setLoading(false));
    }, [userId]);

    if (loading) {
        return <p className="message loading">Se încarcă istoricul...</p>;
    }

    if (error) {
        return <p className="message error">Eroare: {error}</p>;
    }

    return (
        <div className="car-history-container">
            <h2 className="car-history-header">Istoric Comenzi</h2>
            <div className="payment-list">
                {payments.length > 0 ? (
                    payments.map((payment) => (
                        <div key={payment.idPayment} className="payment-item">
                            <p>ID Plată: {payment.idPayment}</p>
                            <p>Adresa de livrare: {payment.shippingAddress}</p>
                            <p>Data livrării: {payment.dateOfDelivery}</p>
                        </div>
                    ))
                ) : (
                    <p>Nu există plăți înregistrate.</p>
                )}
            </div>
            <h3>Mașinile asociate plăților</h3>
            <div className="car-list">
                {cars.length > 0 ? (
                    cars.map((car) => (
                        <div key={car.idCar} className="car-item">
                            <div className="car-image">
                                {car.imageUrl ? (
                                    <img src={car.imageUrl} alt={car.model} />
                                ) : (
                                    <p>Imagine indisponibilă</p>
                                )}
                            </div>
                            <div className="car-info">
                                <h3>{car.model}</h3>
                                <p>Kilometers: {car.kilometers}</p>
                                <p>Release Date: {car.releaseDate}</p>
                                <p>Vehicle Type: {car.vehicleType}</p>
                                <p>Price: {car.price} €</p>
                                <p>Color: {car.color}</p>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>Nu există mașini asociate plăților.</p>
                )}
            </div>
        </div>
    );
}

export default CarHistory;
