import React, { useState } from 'react';
import '../Styles/LogIn.css'; // Asigură-te că ai un fișier CSS pentru stiluri

function LogIn() {
    // State pentru stocarea valorilor introduse în câmpuri
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    // Funcție pentru gestionarea trimiterii formularului
    const handleSubmit = (event) => {
        event.preventDefault();
        console.log('Username:', username);
        console.log('Password:', password);
        // Aici poți adăuga logica de autentificare (ex. trimiterea datelor la backend)
    };

    return (
        <div className="login-container">
            <h2>Autentificare</h2>
            <p>Introdu datele necesare pentru a te autentifica:</p>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="username">Nume utilizator sau Email:</label>
                    <input
                        type="text"
                        id="username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        placeholder="Introdu numele de utilizator sau email-ul"
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Parolă:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Introdu parola"
                        required
                    />
                </div>
                <button type="submit" className="login-button">Autentificare</button>
            </form>
        </div>
    );
}

export default LogIn;
