import React, { useEffect, useState } from 'react';
import CarItem from './CarItem';

function CarList() {
    const [cars, setCars] = useState([]);
    const handleButtonClick = () => {
        useEffect(() =>{

        })
    };
    useEffect(() => {
        fetch("http://localhost:8080/api/v1/car")
            .then(response => {
                if (!response.ok) {
                    throw new Error("Eroare la încărcarea datelor");
                }
                return response.json();
            })
            .then(data => {
                console.log("Data received from backend:", data); // Log pentru verificare
                setCars(data);
            })
            .catch(error => console.error("Eroare:", error));
    }, []);

    return (
        <div>
            <h2>Mașinile Disponibile</h2>
            <div className="button">
                <button onClick={handleButtonClick}>Add Car</button>
            </div>

            <div className="car-list">
            {cars.length > 0 ? (
                    cars.map((car, index) => (
                        <CarItem key={index} car={car}/>
                    ))
                ) : (
                    <p>Nu există mașini disponibile.</p>
                )}
            </div>
        </div>
    );
}
function AddCarForm() {
    const [car, setCar] = useState({
        model: '',
        year: '',
        color: '',
        price: ''
    });

    // Funcția care actualizează datele din formular
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setCar({
            ...car,
            [name]: value
        });
    };

    // Funcția care gestionează trimiterea formularului și cererea POST
    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('http://localhost:8080/api/v1/car', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(car)
            });

            if (response.ok) {
                const data = await response.json();
                console.log('Mașina a fost adăugată cu succes:', data);
                setCar({ model: '', year: '', color: '', price: '' }); // Resetează formularul
            } else {
                console.error('Eroare la adăugarea mașinii:', response.statusText);
            }
        } catch (error) {
            console.error('Eroare de rețea:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit} className="add-car-form">
            <div>
                <label>Model:</label>
                <input
                    type="text"
                    name="model"
                    value={car.model}
                    onChange={handleInputChange}
                    required
                />
            </div>
            <div>
                <label>An fabricație:</label>
                <input
                    type="number"
                    name="year"
                    value={car.year}
                    onChange={handleInputChange}
                    required
                />
            </div>
            <div>
                <label>Culoare:</label>
                <input
                    type="text"
                    name="color"
                    value={car.color}
                    onChange={handleInputChange}
                    required
                />
            </div>
            <div>
                <label>Preț (€):</label>
                <input
                    type="number"
                    name="price"
                    value={car.price}
                    onChange={handleInputChange}
                    required
                />
            </div>
            <button type="submit">Adaugă mașina</button>
        </form>
    );
}
export default CarList;
